iv>                <div class="user_name fl">                    <div class="lf names">手机号</div>                    <div class="rt">                        <input type="text" id="phone" class="ipt" placeholder="请输入您的手机号" name="iphone" value="<?php echo ($info["iphone"]); ?>" onkeyup="value=value.replace(/[^0-9]/g,'')" onpaste="value=value.replace(/[^0-9]/g,'')" oncontextmenu = "value=value.replace(/[^0-9]/g,'')"/>                    </div>                </div>                <div class="user_name fl">                    <div class="lf names">年龄</div>                    <div class="rt">                        <input type="text" class="ipt" placeholder="请输入您的年龄" name="age" value="<?php echo ($info["age"]); ?>" onkeyup="value=value.replace(/[^0-9]/g,'')" onpaste="value=value.replace(/[^0-9]/g,'')" oncontextmenu = "value=value.replace(/[^0-9]/g,'')"/>                    </div>                </div>                <div class="user_iphone fl" style="border-bottom: none">                    <div class="sex lf">性别</div>                    <div class="sex_right rt">                        <div class="right_img lf">                            <?php if($info['sex'] == 2): ?><img src="/public_html/Public/sick/img/succeed.png" class="img-responsive menfolk" alt=""/>                                <?php else: ?>                                <img src="/public_html/Public/sick/img/default.png" class="img-responsive menfolk" alt=""/><?php endif; ?>                        </div>                        <div class="man lf">女</div>                    </div>                    <div class="sex_right rt">                        <div class="right_img lf">                            <?php if($info['sex'] == 1): ?><img src="/public_html/Public/sick/img/succeed.png" class="img-responsive femininity" alt=""/>                                <?php else: ?>                            <img src="/public_html/Public/sick/img/default.png" class="img-responsive femininity" alt=""/><?php endif; ?>                        </div>                        <div class="man lf">男</div>                        <input type="hidden" name="sex" id="doctorsex" />                    </div>                </div>            </form>        </div>        <div class="col-md-12 col-sm-12 col-xs-12">            <button class="button" id="tijiao">确认修改</button>        </div>    </div></div><script src="/public_html/Public/sick/js/jquery-1.11.3.js"></script><script src="/public_html/Public/sick/js/bootstrap.js"></script><script>    $(".femininity").click(function () {        if ($(".menfolk").attr("src") == "/public_html/Public/sick/img/succeed.png") {            $(".menfolk").attr("src", "/public_html/Public/sick/img/default.png")        }        if ($(this).attr("src") == "/public_html/Public/sick/img/default.png") {            $(this).attr("src", "/public_html/Public/sick/img/succeed.png")            $('#doctorsex').val(1);        } else {            $(this).attr("src", "/public_html/Public/sick/img/default.png")        }    })    $(".menfolk").click(function () {        if ($(".femininity").attr("src") == "/public_html/Public/sick/img/succeed.png") {            $(".femininity").attr("src", "/public_html/Public/sick/img/default.png")        }        if ($(this).attr("src") == "/public_html/Public/sick/img/default.png") {            $(this).attr("src", "/public_html/Public/sick/img/succeed.png")            $('#doctorsex').val(2);        } else {            $(this).attr("src", "/public_html/Public/sick/img/default.png")        }    })    $('.user_name').click(function(){        $(this).find('.ipt').focus();    })    $('#tijiao').click(function(){        var phone=document.getElementById("phone").value;        if(phone=="")        {            alert("手机号码不能为空");            return false;        }        else        {            var res=/^(13[0-9]|14[0-9]|15[0-9]|18[0-9])\d{8}$/i;            if(!res.test(phone))            {                alert("手机号码错误");                return false;            }        }        $('form:first').submit();    })</script></body></html>